import React, { useState } from "react";
import "../styles/login.css";
import logo from "../assets/Logo.png";
import bg from "../assets/Giao_dien_dang_nhap/imgbackground.png";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import BASE_URL from "../api"; // chứa http://localhost:5000/api

export default function Login() {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async () => {
    try {
      const res = await axios.post(`${BASE_URL}/auth/login`, {
        username,
        password,
      });

      // ✅ Lưu token và thông tin người dùng
      localStorage.setItem("token", res.data.token);
      localStorage.setItem("account", JSON.stringify(res.data.account));

      navigate("/home");
    } catch (err) {
      alert("Sai tên đăng nhập hoặc mật khẩu!");
      console.error(err);
    }
  };

  return (
    <div className="login-container" style={{ backgroundImage: `url(${bg})` }}>
      <div className="login-left">
        <img src={logo} alt="logo" className="login-logo" />
        <p className="login-desc">
          Quản lý chi tiêu dễ dàng hơn bao giờ hết cùng EMG
        </p>
      </div>

      <div className="login-form-box">
        <label htmlFor="username">Tên tài khoản:</label>
        <input
          type="text"
          id="username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />

        <label htmlFor="password">Mật khẩu:</label>
        <input
          type="password"
          id="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />

        <img
          src={require("../assets/Giao_dien_dang_nhap/nut_dang_nhap.png")}
          alt="Đăng nhập"
          className="login-img-button"
          onClick={handleLogin}
        />

        <p className="login-bottom">
          <span onClick={() => navigate("/register")}>
            Bạn chưa có tài khoản
          </span>
        </p>
      </div>
    </div>
  );
}
