import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import logo from "../assets/login/logo.png";
import background from "../assets/login/background.png";
import btnLogin from "../assets/login/btn_dangnhap.png";
import "../styles/login.css";

export default function Login() {
  const navigate = useNavigate();

  const [message, setMessage] = useState("");
  const [isSuccess, setIsSuccess] = useState(false);

  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async () => {
    try {
      const response = await axios.post(
        "http://localhost:5000/api/auth/login",
        {
          username,
          password,
        }
      );

      const { token, account } = response.data;

      // Lưu token vào localStorage
      localStorage.setItem("token", token);
      localStorage.setItem("user", JSON.stringify(account));

      // Điều hướng sang trang chính
      navigate("/home");
    } catch (error) {
      alert("Đăng nhập thất bại! Vui lòng kiểm tra lại thông tin.");
      console.error("Login error:", error);
    }
  };

  return (
    <div
      className="login-container"
      style={{ backgroundImage: `url(${background})` }}
    >
      <div className="login-box">
        <div className="login-left">
          <img src={logo} alt="Logo" className="login-logo" />
          <p className="login-desc">
            Quản lý chi tiêu dễ dàng hơn <br /> bao giờ hết cùng EMG
          </p>
        </div>

        <div className="login-form-box">
          {message && (
            <div className={`login-message ${isSuccess ? "success" : "error"}`}>
              {message}
            </div>
          )}
          <label htmlFor="username">Tên tài khoản:</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Nhập tài khoản"
          />

          <label htmlFor="password">Mật khẩu:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Nhập mật khẩu"
          />

          <img
            src={btnLogin}
            alt="Đăng nhập"
            className="login-img-button"
            onClick={handleLogin}
          />

          <p className="login-bottom">
            <span onClick={() => navigate("/register")}>
              Bạn chưa có tài khoản
            </span>
          </p>
        </div>
      </div>
    </div>
  );
}
